import React, { Component } from "react";
import Carousel from "react-spring-3d-carousel";
import uuidv4 from "uuid";
import comedy from "../images/comedy.jpg";
import movies from "../images/movies.jpg";
import music from "../images/music.jpg";
import series from "../images/series.jpg";
import cricket from "../images/cricket.jpg";
import gaming from "../images/gaming.jpg";

let slides = [
  {
    key: uuidv4(),
    content: <img src={comedy} alt="1" />
  },
  {
    key: uuidv4(),
    content: <img src={movies} alt="2" />
  },
  {
    key: uuidv4(),
    content: <img src={series} alt="3" />
  },
  {
    key: uuidv4(),
    content: <img src={music} alt="4" />
  },
  {
    key: uuidv4(),
    content: <img src={cricket} alt="5" />
  },
  {
    key: uuidv4(),
    content: <img src={gaming} alt="6" />
  }
];

onChangeInput = (e: React.ChangeEvent<HTMLInputElement>) => {
  this.setState({
    [e.target.name]: parseInt(e.target.value, 10) || 0
  });
};

export class Carousel3d extends Component {
  render() {
    return (
      <div style={{ width: "80%", height: "500px", margin: "0 auto" }}>
        <Carousel slides={slides} />
      </div>
    );
  }
}

export default Carousel3d;
