import React, { Component } from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import comedy from "../images/comedy.jpg";
import music from "../images/music.jpg";
import series from "../images/series.jpg";
import movies from "../images/movies.jpg";
import gaming from "../images/gaming.jpg";
import cricket from "../images/cricket.jpg";

export class Carousel3d extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 300,
      slidesToShow: 4,
      slidesToScroll: 4,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <Slider {...settings}>
        <div>
          <h1>Stand Up Comedy</h1>
        </div>
        <div>
          <img src={music} alt="music" />
        </div>
        <div>
          <img src={series} alt="series" />
        </div>
        <div>
          <img src={movies} alt="movies" />
        </div>
        <div>
          <img src={gaming} alt="gaming" />
        </div>
        <div>
          <img src={cricket} alt="cricket" />
        </div>
      </Slider>
    );
  }
}

export default Carousel3d;
